    private void checkPwdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkPwdActionPerformed
        if(checkPwd.isSelected()){
            txtPwd.setEchoChar((char)0);
        }else{
            txtPwd.setEchoChar('*');
        }
    }//GEN-LAST:event_checkPwdActionPerformed

