    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        try{
         Class.forName("com.mysql.jdbc.Driver");
            java.sql.Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/register","root","");
            
            String sql="SELECT * FROM register1 WHERE UserName=? AND Password=?";
            PreparedStatement pstmt=con.prepareStatement(sql);
            pstmt.setString(1, txtUsername.getText());
            pstmt.setString(2, new String(txtPwd.getPassword()));
            ResultSet rs=pstmt.executeQuery();
                       
            if(rs.next()){
                JOptionPane.showMessageDialog(null,"Login Successful");
                new Menu().setVisible(true);
                this.setVisible(false);
            }else{
                
                JOptionPane.showMessageDialog(null, "Invalid UserName or Password", "Failed", HEIGHT);
            }
            rs.close();
            pstmt.close();
            con.close();
         }catch(Exception e){
             JOptionPane.showMessageDialog(rootPane, e);
         
         }
            if (txtUsername.getText().isEmpty() && String.valueOf(txtPwd.getPassword()).isEmpty()) {
                 JOptionPane.showMessageDialog(this, "Please enter Both Username and Password!", "Warning", JOptionPane.WARNING_MESSAGE);
         }
        
    }//GEN-LAST:event_btnLoginActionPerformed

